!function () {

  var home = document.getElementById('home');
  var dropbtn = document.getElementById('dropbtn');
  var footer = document.getElementById('footer');
  var navClose = document.getElementById('nav-close');
  var navLeft = document.getElementById('nav-left');
  var navRight = document.getElementById('nav-right');

  // If nav-close is clicked and has the class of responsive, change it's class to nav-left
  // Make the nav-right, home, and dropbtn visible
  navClose.onclick = function () {
    var x = document.getElementById('responsive');
    if (x.id === 'nav-left') {
      x.id = 'responsive';
    } else {
      x.id = 'nav-left';
      home.style.display = "block";
      dropbtn.style.display = "block";
      footer.style.display = "block";
      navRight.style.display = "block";
    }
  };

  // If nav-left is clicked and has the class of responsive, change it's class to to nav-left
  // Make the nav-right, home, and dropbtn visible
  navLeft.onclick = function () {
    var x = document.getElementById('responsive');
    if (x.id === 'responsive') {
      x.id = 'nav-left';
      dropbtn.style.display = "block";
      footer.style.display = "block";
      navRight.style.display = "block";
    }
  };
}();

!function () {

  var contact = document.getElementById('contact');
  var footer = document.getElementById('footer');
  var graphics = document.getElementById('graphics');
  var dropbtn = document.getElementById('dropbtn');
  var navRight = document.getElementById('nav-right');
  var photography = document.getElementById('photography');
  var printmaking = document.getElementById('printmaking');
  var videos = document.getElementById('videos');

  // When dropbtn is clicked,
  dropbtn.onclick = function () {
    var x = document.getElementById('nav-left');
    // If nav-left has the class of nav-left, make its only id responsive
    // Give the following divs the class of visible
    if (x.id === 'nav-left') {
      x.id = 'responsive';
      contact.style.display = "none";
      footer.style.display = "none";
      graphics.style.display = "none";
      home.style.display = "none";
      dropbtn.style.display = "none";
      navRight.style.display = "none";
      photography.style.display = "none";
      printmaking.style.display = "none";
      videos.style.display = "none";
    } else {
      // Give the following divs the class of visible
      x.id = 'nav-left';
      contact.style.display = "block";
      footer.style.display = "block";
      graphics.style.display = "block";
      home.style.display = "block";
      dropbtn.style.display = "block";
      navRight.style.display = "block";
      photography.style.display = "block";
      printmaking.style.display = "block";
      videos.style.display = "block";
    }
  };
}();

